#ifndef _EXPANDER_DEFS_H_
#define _EXPANDER_DEFS_H_

// I/O Expander bit values.
// Note: Values from P0_0 through P1_7 must fit in 4-bits for the
//      pin attributes table to work.
#define P0_0 0
#define P0_1 1
#define P0_2 2
#define P0_3 3
#define P0_4 4
#define P0_5 5
#define P0_6 6
#define P0_7 7

#define P1_0 ((1*8)+0)
#define P1_1 ((1*8)+1)
#define P1_2 ((1*8)+2)
#define P1_3 ((1*8)+3)
#define P1_4 ((1*8)+4)
#define P1_5 ((1*8)+5)
#define P1_6 ((1*8)+6)
#define P1_7 ((1*8)+7)

#define P2_0 ((2*8)+0)
#define P2_1 ((2*8)+1)
#define P2_2 ((2*8)+2)
#define P2_3 ((2*8)+3)

#define P3_0 ((3*8)+0)
#define P3_1 ((3*8)+1)
#define P3_2 ((3*8)+2)
#define P3_3 ((3*8)+3)
#define P3_4 ((3*8)+4)
#define P3_5 ((3*8)+5)
#define P3_6 ((3*8)+6)
#define P3_7 ((3*8)+7)

#define P4_0 ((4*8)+0)
#define P4_1 ((4*8)+1)
#define P4_2 ((4*8)+2)
#define P4_3 ((4*8)+3)
#define P4_4 ((4*8)+4)
#define P4_5 ((4*8)+5)
#define P4_6 ((4*8)+6)
#define P4_7 ((4*8)+7)

#define P5_0 ((5*8)+0)
#define P5_1 ((5*8)+1)
#define P5_2 ((5*8)+2)
#define P5_3 ((5*8)+3)

#endif // _EXPANDER_DEFS_H_